import React,{ useEffect,useState } from 'react';
import './App.css';
import SearchIcon from './search.svg';
import MovieCard from './MovieCard';
//2e4cd7a7

const API_URL = 'https://omdbapi.com?apikey=2e4cd7a7';

const App = () =>{

    const [movie, setMovie] = useState([]);

    const [search, setSearch] = useState("");

    const searchBar = (search,items) => {
        if(!search) return items;

        return items.filter((title) => title.name.includes(search));
    }
    const filteredItems= searchBar(search,items);
    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();

        setMovie(data.Search);
        const {items}=data.Search.Title;
        console.log(data.Search);
    }
    useEffect(() =>{
     searchMovies('Batman');
    },[]);
    return(
        <div className='app'>
        <h1>IMDB</h1>
        <div className='search'>
            <input type="text" onChange={e => {setSearch=e.target.value}} />
            <button img src={SearchIcon} alt="SearchIcon"/>
        </div>
        <div className='container'>
        {movie.map((data1) => {
            return(
                <div className='movie'>
                <MovieCard 
                    key={data1.imdbID}
                    title={data1.Title}
                    poster={data1.Poster}
                    type={data1.Type}
                />
                </div>
            )
        })}
        </div>
        </div>
    );
}

export default App;
