import React,{ useEffect,useState } from 'react';
import './App.css';
import SearchIcon from './search.svg';
import MovieCard from './MovieCard';
//2e4cd7a7

const API_URL = 'https://omdbapi.com?apikey=2e4cd7a7';

const App = () =>{

    const [x,setX] = useState(0);
    const [movie, setMovie] = useState([]);

    const [search, setSearch] =useState('');

    const searchItems = (searchValue) => {
        setSearch(searchValue)
        console.log(searchValue);
    }

    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();

        setMovie(data.Search);
        console.log(data.Search);
    }
    useEffect(() =>{
     setX(0);
     searchMovies(search);
    },[]);
    return(
        <div className='app'>
        <h1>IMDB</h1>
        <div className='search'>
            <input type="text" placeholder="Search" onChange={(e)=> searchItems(e.target.value)} />
            <button onClick={()=>setX((prevX)=>prevX+1)}><img src={SearchIcon}/></button>
            <h1>{x}</h1>
            {/* <ul>
            {search.map((value) => (
                    <h2>title={value.Title}</h2>
            )
            )}
            </ul> */}
        </div>
        <div className='container'>
        {movie.map((data1) => {
            return(
                <div className='movie'>
                <MovieCard 
                    key={data1.imdbID}
                    title={data1.Title}
                    poster={data1.Poster}
                    type={data1.Type}
                />
                </div>
            )
        })}
        </div>
        </div>
    );
}

export default App;
