    return(
        <div className='app'>
        <h1>App</h1>
        <MovieCard 
            title='Batman Begins'
            poster='https://m.media-amazon.com/images/M/MV5BOTY4YjI2N2MtYmFlMC00ZjcyLTg3YjEtMDQyM2ZjYzQ5YWFkXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg'
            type='movie'
        />
